// src/pages/HomePage.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMovies = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login'); // Redirige si pas de token
        return;
      }
      try {
        const res = await axios.get('/api/movies', {
          headers: {
            'x-auth-token': token // Envoyer le token dans les en-têtes
          }
        });
        setMovies(res.data);
      } catch (err) {
        console.error(err);
        // Si le token n'est plus valide, l'API renverra une erreur 401
        if (err.response && err.response.status === 401) {
          localStorage.removeItem('token');
          navigate('/login');
        }
      }
    };

    fetchMovies();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div>
      <header style={{ display: 'flex', justifyContent: 'space-between', padding: '20px', backgroundColor: '#333', color: 'white' }}>
        <h1>Ma plateforme de streaming</h1>
        <button onClick={handleLogout}>Déconnexion</button>
      </header>
      <div style={{ padding: '20px' }}>
        <h2>Films disponibles</h2>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
          {movies.length > 0 ? (
            movies.map(movie => (
              <div key={movie._id} style={{ border: '1px solid #ccc', padding: '10px', width: '250px' }}>
                <h3>{movie.title}</h3>
                <p>{movie.description}</p>
                {/* Plus tard, vous pourrez ajouter une image et un lecteur vidéo ici */}
              </div>
            ))
          ) : (
            <p>Aucun film trouvé. Ajoutez-en via l'API back-end.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
